import { useState, useCallback } from 'react';
import { connect } from 'react-redux';
import { Button, Layout, Input, Avatar, Tooltip, Dropdown, Menu } from 'antd';
import getConfig from 'next/config';
import { GithubOutlined, UserOutlined } from '@ant-design/icons';
import Container from '../Container.jsx';

const { Header, Content, Footer } = Layout;
const { Search } = Input;
const { publicRuntimeConfig } = getConfig();

const githubIconStyle = {
    color: 'white',
    fontSize: 40,
    display: 'block',
    paddingTop: 10,
    marginRight: 20
};

const LayoutComponent = ({ children, userInfo }) => {
    const [search, setSearch] = useState('');


    const handeSearchChange = useCallback((event) => {
        setSearch(event.target.value);
    }, [setSearch]);

    const handeOnSearch = useCallback(() => { }, [])

    return (<>
        <Layout>
            <Header>
                <Container renderer={<div className="header-inner" />}>
                    <div className="header-left">
                        <div className="logo">
                            <GithubOutlined style={githubIconStyle} />
                        </div>
                        <div>
                            <Search
                                placeholder="请输入需要检索的内容"
                                value={search}
                                onSearch={handeOnSearch}
                                onChange={handeSearchChange}
                                style={{ width: 200 }}
                            />
                        </div>
                    </div>
                    <div className="header-right">
                        <div className="user">
                            {
                                userInfo && userInfo.id ? <a href="/">
                                    <Avatar size={40} src={userInfo.avatar_url} />
                                </a> : <Tooltip title="点击进行登录"><a href={publicRuntimeConfig.OAUTH_URL}>
                                        <Avatar size={40} icon={<UserOutlined />} />
                                    </a>
                                    </Tooltip>
                            }
                        </div>
                    </div>
                </Container>
            </Header>
            <Content><Container>{children}</Container></Content>
            <Footer>
                <div className="header-footer">
                    Develop by zongxiaobai@@163.com<a href="mailto:xxxxxxxx@163.com"></a>
                </div>
            </Footer>
            <style jsx>{`
                        .header-inner{
                            display: flex;
                            justify-content: space-between;
                        }
                        .header-left{
                            display: flex;
                            justify-content: flex-start;
                        }
                        .header-footer{
                            text-align: center
                        }
            `}</style>
            <style jsx global>
                {`
                            #__next{
                                height: 100%
                            }
                            .ant-layout{
                                height: 100%
                            }
                            .ant-layout-header{
                                padding-left: 0;
                                padding-right: 0
                            }
                        `}
            </style>
        </Layout>
    </>)
}

function mapStateToProps(state) {
    return {
        userInfo: state.userInfo
    }
}
export default connect(mapStateToProps)(LayoutComponent);
