import { useState, useCallback } from 'react';

import { Button, Layout, Input, Avatar } from 'antd';
import { GithubOutlined, UserOutlined } from '@ant-design/icons';
import Container from '../Container.jsx';

const { Header, Content, Footer } = Layout;
const { Search } = Input;

const githubIconStyle = {
    color: 'white',
    fontSize: 40,
    display: 'block',
    paddingTop: 10,
    marginRight: 20
};

export default ({ children }) => {
    const [search, setSearch] = useState('');


    const handeSearchChange = useCallback((event) => {
        setSearch(event.target.value);
    }, [setSearch]);

    const handeOnSearch = useCallback(() => { }, [])

    return (<>
        <Layout>
            <Header>
                <div className="header-inner">
                    <div className="header-left">
                        <div className="logo">
                            <GithubOutlined style={githubIconStyle} />
                        </div>
                        <div>
                            <Search
                                placeholder="请输入需要检索的内容"
                                value={search}
                                onSearch={handeOnSearch}
                                onChange={handeSearchChange}
                                style={{ width: 200 }}
                            />
                        </div>
                    </div>
                    <div className="header-right">
                        <div className="user">
                            <Avatar size={40} icon={<UserOutlined />} />
                        </div>
                    </div>
                </div>
            </Header>
            <Content>{children}</Content>
            <Footer>
                <div className="header-footer">
                      Develop by zongxiaobai@@163.com<a href="mailto:xxxxxxxx@163.com"></a>
                </div>
            </Footer>
            <style jsx>{`
                        .header-inner{
                            display: flex;
                            justify-content: space-between;
                        }
                        .header-left{
                            display: flex;
                            justify-content: flex-start;
                        }
                        .header-footer{
                            text-align: center
                        }
            `}</style>
            <style jsx global>
                        {`
                            #__next{
                                height: 100%
                            }
                            .ant-layout{
                                height: 100%
                            }
                        `}
            </style>
        </Layout>
    </>)
}
