import { useEffect } from 'react';
import axios from 'axios';
import Link from 'next/link';
import Router from 'next/router';
import getConfig from 'next/config';
import { Button } from 'antd';
import { connect } from 'react-redux';

const { publicRuntimeConfig } = getConfig();

const Index = ({ count, add }) => {

    function goToB() {
        // Router.push('/b');
        Router.push({
            pathname: '/b',
            query: {
                id: 2
            }
        }, '/b/2');
    }

    useEffect(() => {
       axios.get('/api/user/info').then((res) => {
        console.log('请求到的用户信息', res);
       });
    }, [])

    return (
        <>
            <Link href="/a?id=1" as="/a/1">
                <Button type="primary">跳转Ay</Button>
            </Link>
            <Button onClick={goToB} type="dashed">跳转B</Button>
            <div>
                <Button onClick={() => add()} className="bt" style={{ marginTop: 20}}>点击count加1</Button>
                <div className="box">
                    redux-store传递的值 count:{count}
                </div>
            </div>
            <a href={publicRuntimeConfig.OAUTH_URL}>登录</a>
            <style jsx>{`.box{margin-top: 20px;}`}</style>
        </>
    );
}

function mapStateToProps(state) {
    return {
        count: state.counter.count
    }
}

function mapDispatchToProps(dispatch) {
    return {
        add: () => dispatch({ type: 'add' })
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Index);
