const { request } = require('../lib/api.js');
const Index = ({ userRepos }) => {
    console.log('userRepos', userRepos);
    return <span>Index</span>
}

Index.getInitialProps = async ({ ctx }) => {
    const result = await request(
        {
        url: '/user/repos',
        },
        ctx.req,
        ctx.res
        );
    return {
        userRepos: result
    }
}

export default Index;
