import App from 'next/app';
import { Provider } from 'react-redux';
import 'antd/dist/antd.css';
import Layout from '../components/layout/layout.js';
import MyContext from '../lib/my-context.js';
// import store from '../store/store.js';
import WithReduxHoc from '../lib/witht-redux.js';


class MyApp extends App {
    static async getInitialProps(ctx) {
        const { Component } = ctx;
        let pageProps = {};
        console.log('getInitialProps------_app');
        if (Component.getInitialProps) {
            pageProps = await Component.getInitialProps(ctx);
        }
        return {
            pageProps
        }
    }

    render() {
        const { Component, pageProps, reduxStore } = this.props;
        console.log('render----_app');
        return (<Layout>
            <Provider store={reduxStore}>
            <MyContext.Provider value="myContext-provider">
                <Component {...pageProps} />
            </MyContext.Provider>
            </Provider>
        </Layout>);
    }
}

export default WithReduxHoc(MyApp);
