import App from 'next/app';
import { Provider } from 'react-redux';
import 'antd/dist/antd.css';
import Router from 'next/router';
import Layout from '../components/layout/layout.js';
import PageLoadding from '../components/PageLoadding.jsx';
import WithReduxHoc from '../lib/witht-redux.js';


class MyApp extends App {
    state = {
        loadding: false
    };

    static async getInitialProps(ctx) {
        const { Component } = ctx;
        let pageProps = {};
        if (Component.getInitialProps) {
            pageProps = await Component.getInitialProps(ctx);
        }
        return {
            pageProps
        }
    }

    startLoadding() {
        this.setState({
            loadding: true
        });
    }

    stopLoadding() {
        this.setState({
            loadding: true
        });
    }

    // TODO: 组件加载
    componentDidMount() {
        Router.events.on('routerChangeStart', );
        Router.events.on('routerChangeComplete', ); 
        Router.events.on('routerChangeError', );
    }

    // TODO: 组件卸载前
    componentWillUnmount() {

    }

    render() {
        const { Component, pageProps, reduxStore } = this.props;
        return (<Provider store={reduxStore}>
            <PageLoadding />
            <Layout>
                <Component {...pageProps} />
            </Layout>
        </Provider>);
    }
}

export default WithReduxHoc(MyApp);
