import { withRouter } from 'next/router';
import styled from 'styled-components';

const Title = styled.h1`
color: #ccc;
font-size: 20px
`
const A = ({ router, name, flag }) => {
    console.log('render---a页面');
    return <>
        <Title>style-component</Title>
        <span className="acss">A页面{router.query.id}{name}{flag}</span>
        <style jsx>{`.acss{color: green}`}</style>
    </>
}
A.getInitialProps = async (ctx) => {
    const moment = await import('moment');
    console.log('getInitialProps------a页面');
    return { name: 'opop'}
} 
export default withRouter(A);
