import { useState, useEffect, useReducer, useContext, useRef } from 'react';
import MyContext from '../lib/my-context.js';

function reducerCount(state, action) {
    switch (action.type) {
        case "add":
            return state = state + 1;
        case "min":
            return state = state - 1;
        default:
            return state;
    }
}

function B() {
    const [count, dispatchCount] = useReducer(reducerCount, 0);
    const context = useContext(MyContext);
    // TODO: bss Ref 在某一个节点上面的ref属性上面赋值，然后直接调用bssRef 进行节点元素获取
    const bssRef = useRef();

    useEffect(() => {
        console.log('组件加载完成');
        const time = setInterval(() => {
            dispatchCount({ type: 'add' });
        }, 1000);
        return () => { console.log('B页面卸载了'); clearInterval(time); }
    }, [])
    return <>
        <span>页面B{count}</span>
        <div>context传值 展示{context}</div>
    </>
}

B.getInitialProps = ({ req }) => {
    console.log('这是B页面');
    return { name: 'pll' }
}

export default B;


