import { createStore, combineReducers, applyMiddleware } from 'redux';
import ReduxThunk from 'redux-thunk';
import { composeWithDevTools } from 'redux-devtools-extension';


// TODO: 定义inntstate
let countState = { count: 2 };
let nameState = { name: 'jim', sex: 'man' };

// TODO: 定义reducer

const ADD = "add";
function countReducer(state = countState, action) {
        switch (action.type) {
            case ADD:
                return {
                    ...state,
                    count: state.count + 1
                }
            default:
                return state;
        }
}

const MIN = "name";
function nametReducer(state = nameState, action) {
    // console.log('nametReducer', action);
        switch (action.type) {
            case MIN:
                return {
                    ...state,
                    name: action.name
                }
            default:
                return state;
        }
}

// TODO: redux-thunk 测试
function asyncThunk(num) {
    return (dispatch, getState) => {
        setTimeout(() => {
            // dispatch()
        }, 1000)
    }
}
const allReducer = combineReducers({ counter: countReducer, user: nametReducer });

export default function initializeStore(state) {
    const store = createStore(allReducer, Object.assign({}, state), composeWithDevTools(applyMiddleware(ReduxThunk)));
    return store;
}
