import { Button } from 'antd';
import getConfig from 'next/config';
const { request } = require('../lib/api.js');
const { publicRuntimeConfig } = getConfig();

const Index = ({ resultRepos, resultStaredRepos, isLogin }) => {
    console.log('userRepos', resultRepos, resultStaredRepos);
    if (!isLogin) {
        return <div className="root">
            <p>请先登录</p>
            <Button type="primary" href={publicRuntimeConfig.OAUTH_URL}>登录</Button>
            <style jsx>
                {
                    `.root{
                        height: 400px;
                        display: flex;
                        justify-content: center;
                        aligin-items: center;
                    }
                    `
                }
             </style>   
        </div>
    }

    return <span>Index</span>
}

Index.getInitialProps = async ({ ctx, reduxStore }) => {

    const userInfo = reduxStore.getState.userInfo;
    if (!userInfo || !userInfo.id) {
        return {
            isLogin: false
        }
    }

    const resultRepos = await request(
        {
        url: '/user/repos',
        },
        ctx.req,
        ctx.res
        );
    const resultStaredRepos = await request(
        {
            url: '/user/starred'
        },
        ctx.req,
        ctx.res
    )
    return {
        isLogin: true,
        resultRepos: resultRepos.data,
        resultStaredRepos: resultStaredRepos.data
    }
}

export default Index;
