import { Button } from 'antd';
import { MailOutlined } from '@ant-design/icons';
import { connect } from 'react-redux';
import getConfig from 'next/config';
const { request } = require('../lib/api.js');
const { publicRuntimeConfig } = getConfig();

const Index = ({ resultRepos, resultStaredRepos, userInfo }) => {
    console.log('userRepos', resultRepos, resultStaredRepos);
    if (!userInfo || !userInfo.id) {
        return <div className="root">
            <p>请先登录
            <Button type="primary" href={publicRuntimeConfig.OAUTH_URL}>登录</Button>
            </p>
            <style jsx>
                {
                    `.root{
                        height: 400px;
                        display: flex;
                        justify-content: center;
                        align-items: center;
                    }
                    `
                }
            </style>
        </div>
    }

    return <div className="root">
        <div className="user-info">
            <img src={userInfo.avatar_url} alt="头像" className="avatar" />
            <span className="login">{userInfo.login}</span>
            <span className="name">{userInfo.name}</span>
            <span className="bio">{userInfo.bio}</span>
            <p className="email">
                <MailOutlined style={{ marginRight: 10 }} />
                <a href={`mailto:${userInfo.email}`}>{userInfo.email}</a>
            </p>
        </div>
    </div>
}

Index.getInitialProps = async ({ ctx, reduxStore }) => {

    const userInfo = reduxStore.getState.userInfo;
    if (!userInfo || !userInfo.id) {
        return {
            isLogin: false
        }
    }

    const resultRepos = await request(
        {
            url: '/user/repos',
        },
        ctx.req,
        ctx.res
    );
    const resultStaredRepos = await request(
        {
            url: '/user/starred'
        },
        ctx.req,
        ctx.res
    )
    return {
        isLogin: true,
        resultRepos: resultRepos.data,
        resultStaredRepos: resultStaredRepos.data
    }
}

function mapStateToProps(state) {
    return {
        userInfo: state.userInfo
    }
}

export default connect(mapStateToProps)(Index);
