import { createStore, combineReducers, applyMiddleware } from 'redux';
import ReduxThunk from 'redux-thunk';
import { composeWithDevTools } from 'redux-devtools-extension';


// TODO: 定义inntstate
let userState = {};

// TODO: 定义reducer

const MIN = "name";
function usertReducer(state = userState, action) {
        switch (action.type) {
            case MIN:
                return {
                    ...state,
                    name: action.name
                }
            default:
                return state;
        }
}

// TODO: redux-thunk 测试
function asyncThunk(num) {
    return (dispatch, getState) => {
        setTimeout(() => {
            // dispatch()
        }, 1000)
    }
}
const allReducer = combineReducers({ userInfo: usertReducer });

export default function initializeStore(state) {
    const store = createStore(allReducer, Object.assign({}, state), composeWithDevTools(applyMiddleware(ReduxThunk)));
    return store;
}
